const Message = () => {

    useEffect(() => {

        window.addEventListener('mousemove', (e) => {
            const coors = { x: e.x, y: e.y };
            console.log();
        });
        
        return () => {
            // Cleanup
            console.log('Component unmounted');
        }
    }, []);

    return (
        <>
            <h1>You're cool.</h1> 
        </>
    )
}

export default Message
