import React from 'react';
import { UserContext } from './UserContext';

const LoginScreen = () => {

    const { setUser } =  useContext(UserContext);

    const user = {
        id: 1234,
        name: 'Fenr'
    }

    return (
        <div>
            <h1>Login Screen</h1>
            <hr />

            <button
                className="btn btn-primary"
                onClick={ () => setUser() }
            >
                Login
            </button>
        </div>
    );
}

export default LoginScreen;
