    const [state, setState] = useState({
        data: null,
        loading: true,
        error: null
    });

    useEffect(() => {

        setState({
            data: null,
            loading: true,
            error: null
        });
        
        fetch(url)
            .then(res => res.json())
            .then(data => {
                setTimeout(() => {
                    setState({
                        loading: false,
                        error: null,
                        data
                    });
                }, 4000);
            })
    }, [url]);

    return state;
}

