import { useState } from 'react';
import './counter.css';

const CounterApp = () => {

    const [{ counter1, counter2}, setCounter] = useState({
        counter1: 10,
        counter2: 20
    });

    console.log(counter);

    return (
        <>
            {/* <h1>Counter { counter }</h1> */}
            <hr />

            <button
                className="btn btn-primary"
                onClick={ ()  => {
                    setCounter(counter + 1)
                }}
            >+1</button>
        </>
    )
}


export default CounterApp
