import '../02-useEffect/effects.css';

const FocusScreen = () => {

    const handleClick = () => {
        document.querySelector('input').focus();
    }
    
    return (
        <div>
            <h1>Focus Screen</h1>
            <hr />

            <input
                type="text"
                className="form-control"
                placeholder="Su nombre"
            />

            <button
                className="btn btn-outline-primary mt-5"
                onClick={ handleClick }
            >
                Focus
            </button>
        </div>
    )
}

export default FocusScreen;
